INSERT INTO `book` VALUES (2,'giao-tiep-giua-php-va-mysql','Giao tiếp giửa PHP và MYSQL','','<p style=\"text-align:justify\">dang xay dung...</p>\r\n','<p style=\"text-align:justify\">​dang xay dung...</p>\r\n\r\n<p>&nbsp;</p>\r\n',1,'1',1455771234,'phpmysql.png'),(3,'khai-niem-va-thuc-hanh-html','Khái niệm và thực hành HTML','<iframe width=\"100%\" height=\"480\" src=\"https://www.youtube.com/embed/4Z0zKWVVXYA\" frameborder=\"0\" allowfullscreen></iframe>','Các khái niệm về mã HTML, giúp người lập trình bắt đầu nhập môn vào học lập trình website.','<p style=\"text-align:justify\">Để bắt đầu học ng&ocirc;n ngữ HTML th&igrave; m&igrave;nh khuy&ecirc;n c&aacute;c bạn/em t&igrave;m hiểu n&oacute; ở&nbsp;<a href=\"http://www.w3schools.com/html/html_intro.asp\" target=\"_blank\">http://www.w3schools.com/</a></p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Các khái niệm về mã HTML, giúp người lập trình bắt đầu nhập môn vào học lập trình website.\" src=\"http://hocwebsite.org/uploads/w3school.png\" style=\"width:80%\" title=\"Khái niệm và thực hành HTML\" /></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Kh&aacute;i niệm HTML l&agrave; g&igrave;?</strong></p>\r\n\r\n<p>HTML l&agrave; một ng&ocirc;n ngữ đ&aacute;nh dấu cho việc m&ocirc; tả c&aacute;c t&agrave;i liệu website.</p>\r\n\r\n<ul>\r\n	<li>HTML l&agrave; viết tắt của <strong>Hypertext Markup Language</strong></li>\r\n	<li>Một ng&ocirc;n ngữ đ&aacute;nh dấu l&agrave; một tập hợp c&aacute;c thẻ đ&aacute;nh dấu</li>\r\n	<li>T&agrave;i liệu HTML được m&ocirc; tả bằng c&aacute;c thẻ HTML</li>\r\n	<li>Mỗi thẻ HTML m&ocirc; tả nội dung t&agrave;i liệu kh&aacute;c nhau</li>\r\n</ul>\r\n\r\n<p><strong>V&iacute; dụ ta c&oacute; nội dung của một trang HTML đơn giản sau</strong></p>\r\n\r\n<p><span style=\"color:blue\">&lt;</span><span style=\"color:brown\">!DOCTYPE</span><span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><span style=\"color:red\">html</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">html</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">head</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">title</span><span style=\"color:blue\">&gt;</span>Ti&ecirc;u đề website của bạn<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/title</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/head</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">body</span><span style=\"color:blue\">&gt;</span><br />\r\n<br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">h1</span><span style=\"color:blue\">&gt;</span><span style=\"color:rgb(0, 0, 0)\">Ti&ecirc;u đề nội dung b&agrave;i viết của bạn</span><span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/h1</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">p</span><span style=\"color:blue\">&gt;</span><span style=\"color:rgb(0, 0, 0)\">C&aacute;c nội dung văn bản</span><span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/p</span><span style=\"color:blue\">&gt;</span><br />\r\n<br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/body</span><span style=\"color:blue\">&gt;</span><br />\r\n<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/html</span><span style=\"color:blue\">&gt;</span></p>\r\n\r\n<p><strong>Giải th&iacute;ch v&iacute; dụ tr&ecirc;n&nbsp;</strong></p>\r\n\r\n<ul>\r\n	<li>Việc khai b&aacute;o <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">!DOCTYPE</span><span style=\"color:rgb(0, 0, 0)\">&nbsp;</span><span style=\"color:red\">html</span><span style=\"color:blue\">&gt;</span> định nghĩa kiểu t&agrave;i liệu cho tr&igrave;nh duyệt, cụ thể&nbsp;đ&acirc;y l&agrave; khai b&aacute;o của HTML5.</li>\r\n	<li>Những nội dung nằm giửa&nbsp;<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">html</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/html</span><span style=\"color:blue\">&gt;</span> m&ocirc; tả to&agrave;n bộ&nbsp;t&agrave;i liệu&nbsp;HTML.</li>\r\n	<li>Nằm giửa&nbsp;<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">head</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/head</span><span style=\"color:blue\">&gt;</span> cung cấp th&ocirc;ng tin&nbsp;v&agrave;&nbsp;định dạng t&agrave;i liệu cho website của bạn.</li>\r\n	<li>Nằm giửa <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">title</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/title</span><span style=\"color:blue\">&gt;</span> l&agrave; ti&ecirc;u đề cho một trang website của bạn.</li>\r\n	<li>Nằm giửa <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">body</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/body</span><span style=\"color:blue\">&gt;</span> m&ocirc; tả nội dung trang c&oacute; thể nh&igrave;n thấy trong website của bạn.</li>\r\n	<li>Nằm giửa <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">h1</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/h1</span><span style=\"color:blue\">&gt;</span> thường l&agrave; ti&ecirc;u đề b&agrave;i viết của bạn.</li>\r\n	<li>Nằm giửa&nbsp;<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">p</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/p</span><span style=\"color:blue\">&gt;</span> m&ocirc; tả một đoạn văn, trong b&agrave;i viết của bạn.</li>\r\n</ul>\r\n\r\n<p><strong>C&aacute;c thẻ&nbsp;HTML</strong></p>\r\n\r\n<p>C&aacute;c thẻ HTML l&agrave; c&aacute;c từ kh&oacute;a (t&ecirc;n thẻ) được bao quanh bởi c&aacute;c dấu ngoặc nhọn:</p>\r\n\r\n<p><span style=\"color:blue\">&lt;</span><span style=\"color:brown\">t&ecirc;n thẻ</span><span style=\"color:blue\">&gt;</span>&nbsp;nội dung&nbsp;<span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/t&ecirc;n thẻ</span><span style=\"color:blue\">&gt;</span></p>\r\n\r\n<ul>\r\n	<li>C&aacute;c thẻ HTML th&ocirc;ng thường đi theo cặp như <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">p</span><span style=\"color:blue\">&gt;</span> v&agrave; <span style=\"color:blue\">&lt;</span><span style=\"color:brown\">/p</span><span style=\"color:blue\">&gt;</span>.</li>\r\n	<li><span style=\"color:blue\">&lt;</span><span style=\"color:brown\">t&ecirc;n thẻ</span><span style=\"color:blue\">&gt;</span>&nbsp; l&agrave; bắt đầu, c&ograve;n <span style=\"color:blue\">&lt;/</span><span style=\"color:brown\">t&ecirc;n thẻ</span><span style=\"color:blue\">&gt;</span>&nbsp;l&agrave; kết th&uacute;c thẻ.</li>\r\n</ul>\r\n\r\n<p><strong>Cấu tr&uacute;c một trang HTML&nbsp;</strong></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:80%\" summary=\"Cấu trúc chuẩn của một trang HTML\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><span style=\"color:rgb(0, 0, 0)\">&lt;html&gt;</span></p>\r\n\r\n			<table border=\"0\" cellpadding=\"10\" cellspacing=\"1\" style=\"background:#FFF; margin-left:30px; width:80%\" summary=\"Phần khai báo và định dạng một trang HTML\">\r\n				<tbody>\r\n					<tr>\r\n						<td>\r\n						<p><span style=\"color:rgb(0, 0, 0)\">&lt;head&gt;</span></p>\r\n\r\n						<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin-left:30px; width:80%\" summary=\"Đây là tiêu đề của một website\">\r\n							<tbody>\r\n								<tr>\r\n									<td><span style=\"color:rgb(0, 0, 0)\">&lt;title&gt;Ti&ecirc;u đề trang website&lt;/title&gt;</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n\r\n						<p><span style=\"color:rgb(0, 0, 0)\">&lt;/head&gt;</span></p>\r\n						</td>\r\n					</tr>\r\n					<tr>\r\n						<td>\r\n						<p><span style=\"color:rgb(0, 0, 0)\">&lt;body&gt;</span></p>\r\n\r\n						<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin-left:30px; width:80%\">\r\n							<tbody>\r\n								<tr>\r\n									<td><span style=\"color:rgb(0, 0, 0)\">&lt;h1&gt;Ti&ecirc;u đề của b&agrave;i viết &lt;/h1&gt;</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n\r\n						<p>&nbsp;</p>\r\n\r\n						<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin-left:30px; width:80%\">\r\n							<tbody>\r\n								<tr>\r\n									<td><span style=\"color:rgb(0, 0, 0)\">&lt;p&gt;Đoạn văn bản đầu &lt;/p&gt;</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n\r\n						<p>&nbsp;</p>\r\n\r\n						<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"margin-left:30px; width:80%\">\r\n							<tbody>\r\n								<tr>\r\n									<td><span style=\"color:rgb(0, 0, 0)\">&lt;p&gt;C&aacute;c đoạn văn bản tiếp theo&lt;/p&gt;</span></td>\r\n								</tr>\r\n							</tbody>\r\n						</table>\r\n\r\n						<p><span style=\"color:rgb(0, 0, 0)\">&lt;/body&gt;</span></p>\r\n						</td>\r\n					</tr>\r\n				</tbody>\r\n			</table>\r\n\r\n			<p><span style=\"color:rgb(0, 0, 0)\">&lt;/html&gt;</span></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>Lưu &yacute; </strong>l&agrave; chỉ m&igrave;nh nội dung nằm trong&nbsp;thẻ&nbsp;<span style=\"color:rgb(0, 0, 0)\">&lt;body&gt; v&agrave;&nbsp;&lt;/body&gt; l&agrave; mới được hiển thị ra tr&igrave;nh duyệt cho ch&uacute;ng ta xem m&agrave; th&ocirc;i.</span></p>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0)\"><strong>C&ograve;n khai b&aacute;o</strong>&nbsp;</span>&lt;!DOCTYPE&gt;</p>\r\n\r\n<p>C&aacute;c &lt;! DOCTYPE&gt; khai gi&uacute;p tr&igrave;nh duyệt hiển thị một trang website của bạn một c&aacute;ch ch&iacute;nh x&aacute;c về&nbsp;loại (<a href=\"https://vi.wikipedia.org/wiki/XHTML\" target=\"_blank\">XHTML</a>:&nbsp;<em>Extensible HyperText Markup Language&nbsp;</em>hay HTML&nbsp;) v&agrave; phi&ecirc;n bản của n&oacute;.</p>\r\n\r\n<p>V&agrave; bạn khai b&aacute;o như thế n&agrave;o th&igrave; tất cả c&aacute;c khai b&aacute;o đ&oacute; đều được chấp nhận.</p>\r\n\r\n<p><strong>C&aacute;c kiểu khai b&aacute;o gồm</strong>&nbsp;</p>\r\n\r\n<p><strong>HTML5</strong> sẽ khai b&aacute;o l&agrave;&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:80%\" summary=\"Khai báo nội dung HTML5 cho trình duyệt \">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"color:rgb(0, 0, 0)\">&nbsp;&lt;!DOCTYPE html&gt;</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>HTML 4.01</strong></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"line-height:20.8px; width:100%\" summary=\"Khai báo nội dung HTML5 cho trình duyệt \">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"color:rgb(0, 0, 0)\">&nbsp; &lt;!DOCTYPE HTML PUBLIC &quot;-//W3C//DTD HTML 4.01 Transitional//EN&quot; &quot;http://www.w3.org/TR/html4/loose.dtd&quot;&gt;</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p><strong>XHTML 1.0&nbsp;</strong></p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"line-height:20.8px; width:100%\" summary=\"Khai báo nội dung HTML5 cho trình duyệt \">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style=\"color:rgb(0, 0, 0)\">&nbsp; &lt;!DOCTYPE html PUBLIC &quot;-//W3C//DTD XHTML 1.0 Transitional//EN&quot; &quot;http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd&quot;&gt;</span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>V&agrave; cuối c&ugrave;ng c&aacute;c <strong>phi&ecirc;n bản HTML</strong> l&agrave;&nbsp;</p>\r\n\r\n<table border=\"0\" cellpadding=\"1\" cellspacing=\"1\" style=\"width:80%\">\r\n	<thead>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\"><strong>Phi&ecirc;n bản&nbsp;</strong></th>\r\n			<th scope=\"col\" style=\"text-align:left\"><strong>Năm&nbsp;</strong></th>\r\n		</tr>\r\n	</thead>\r\n	<tbody>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\"><strong>HTML</strong></th>\r\n			<td><strong>1991</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\"><strong>HTML 2.0</strong></th>\r\n			<td><strong>1995</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\">HTML 3.2&nbsp;</th>\r\n			<td><strong>1997</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\">HTML 4.0.1</th>\r\n			<td><strong>1999</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\">XHTML</th>\r\n			<td><strong>2000</strong></td>\r\n		</tr>\r\n		<tr>\r\n			<th scope=\"row\" style=\"text-align:left\"><strong>HTML 5</strong></th>\r\n			<td><strong>2014</strong></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n',1,'1',1456465183,'HTML5.png'),(4,'cai-dat-netbeans-ide-phuc-vu-viet-ma-php','Cài đặt NETBEANS IDE  phục vụ viết mã PHP','<iframe title=\"Cài đặt NETBEANS IDE  phục vụ viết mã PHP\" width=\"100%\" height=\"480\" src=\"https://www.youtube.com/embed/LbzFgnpJuqc\" frameborder=\"0\" allowfullscreen><p>Cài đặt NETBEANS IDE  phục vụ viết mã PHP</p></iframe>','Cài đặt công cụ hỗ trợ lập trình website là NETBEANS IDE . Giúp người lập trình ngôn ngữ PHP dễ dàng tạo projects.','<p style=\"text-align:justify\"><strong>Kh&aacute;i niệm IDE &nbsp;(</strong><a href=\"https://vi.wikipedia.org/wiki/M%C3%B4i_tr%C6%B0%E1%BB%9Dng_ph%C3%A1t_tri%E1%BB%83n_t%C3%ADch_h%E1%BB%A3p\" target=\"_blank\">https://vi.wikipedia.org</a><strong>)</strong></p>\r\n\r\n<p style=\"text-align:justify\">Ch&iacute;nh l&agrave;<strong> M&ocirc;i trường ph&aacute;t triển t&iacute;ch hợp</strong><span style=\"color:rgb(37, 37, 37)\">&nbsp;(</span><a href=\"https://vi.wikipedia.org/wiki/Ti%E1%BA%BFng_Anh\" style=\"text-decoration: none; color: rgb(11, 0, 128); font-family: sans-serif; font-size: 14px; line-height: 22.4px; background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\" target=\"_blank\" title=\"Tiếng Anh\">tiếng Anh</a><span style=\"color:rgb(37, 37, 37)\">:&nbsp;</span><em>Integrated Development Environment</em><span style=\"color:rgb(37, 37, 37)\">; viết tắt:&nbsp;</span><em>IDE</em><span style=\"color:rgb(37, 37, 37)\">) c&ograve;n được gọi l&agrave; &quot;</span><em>M&ocirc;i trường thiết kế hợp nhất</em><span style=\"color:rgb(37, 37, 37)\">&quot; (tiếng Anh:&nbsp;</span><em>Integrated Design Environment</em><span style=\"color:rgb(37, 37, 37)\">) hay &quot;</span><em>M&ocirc;i trường gỡ lỗi hợp nhất</em><span style=\"color:rgb(37, 37, 37)\">&quot; (tiếng Anh:</span><em>Integrated Debugging Environment</em><span style=\"color:rgb(37, 37, 37)\">) l&agrave; một loại&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/Ph%E1%BA%A7n_m%E1%BB%81m\" style=\"text-decoration: none; color: rgb(11, 0, 128); font-family: sans-serif; font-size: 14px; line-height: 22.4px; background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\" target=\"_blank\" title=\"Phần mềm\">phần mềm m&aacute;y t&iacute;nh</a><span style=\"color:rgb(37, 37, 37)\">&nbsp;c&oacute; c&ocirc;ng dụng gi&uacute;p đỡ c&aacute;c&nbsp;</span><a href=\"https://vi.wikipedia.org/wiki/L%E1%BA%ADp_tr%C3%ACnh_vi%C3%AAn\" style=\"text-decoration: none; color: rgb(11, 0, 128); font-family: sans-serif; font-size: 14px; line-height: 22.4px; background-image: none; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\" target=\"_blank\" title=\"Lập trình viên\">lập tr&igrave;nh vi&ecirc;n</a><span style=\"color:rgb(37, 37, 37)\">&nbsp;trong việc ph&aacute;t triển phần mềm.</span></p>\r\n\r\n<p style=\"text-align:justify\">V&agrave; NETBEANS IDE l&agrave;&nbsp;<span style=\"color:rgb(37, 37, 37)\">m&ocirc;i trường ph&aacute;t triển hợp nhất nhiều ng&ocirc;n ngữ: c&oacute; thể l&agrave;m việc với nhiều ng&ocirc;n ngữ lập tr&igrave;nh như JAVA, C/C++, PHP ...</span></p>\r\n\r\n<p style=\"text-align:justify\">Để c&agrave;i đặt&nbsp;NETBEANS IDE&nbsp;ta cần l&agrave;m c&aacute;c bước sau.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Bước 1</strong>&nbsp;ch&uacute;ng ta tải NETBEANS về ở&nbsp;<a href=\"https://netbeans.org/downloads/index.html\" target=\"_blank\">đ&acirc;y</a>&nbsp;ta chọn PHP v&agrave; nếu windows bạn l&agrave; 32bit th&igrave; ta chọn Download x86, c&ograve;n windows của bạn l&agrave; 64bit th&igrave; ta chọn bản&nbsp;Download x64.</p>\r\n\r\n<p style=\"margin-left:40px; text-align:justify\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans1.jpg\" style=\"width:80%\" /></p>\r\n\r\n<p><strong>Bước 2</strong>&nbsp;c&agrave;i đặt JDK ở&nbsp;<a href=\"http://www.oracle.com/technetwork/java/javase/downloads/jdk7-downloads-1880260.html\" target=\"_blank\">đ&acirc;y</a>&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\">Sau đ&oacute; c&aacute;c bạn k&eacute;o xuống v&agrave; chọn&nbsp;<span style=\"color:rgb(255, 0, 0)\"><strong>Accept License Agreement&nbsp;</strong></span></p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans2.jpg\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">Chọn bản th&iacute;ch hợp cho Windows của m&igrave;nh&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans3.jpg\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">Sau đ&oacute; phụ thuộc v&agrave;o windows của bạn l&agrave; 32 bit th&igrave; bạn chọn&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"color:rgb(0, 0, 0)\">Windows x86 |&nbsp;138.31 MB &nbsp;|&nbsp;</span>jdk-7u79-windows-i586.exe</p>\r\n\r\n<p style=\"margin-left:40px\">Nếu l&agrave; windows 64 bit th&igrave; bạn chọn&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><span style=\"color:rgb(0, 0, 0)\">Windows x64 |&nbsp;140.06 MB |</span><span style=\"background-color:rgb(229, 229, 229); color:rgb(0, 0, 0)\">&nbsp;</span>jdk-7u79-windows-x64.exe</p>\r\n\r\n<p style=\"margin-left:40px\">click v&agrave; đường link v&agrave; tải về</p>\r\n\r\n<p><strong>Bước 3 tiến h&agrave;nh c&agrave;i đặt&nbsp;</strong></p>\r\n\r\n<p style=\"margin-left:40px\">Trước ti&ecirc;n bạn phải <strong>c&agrave;i đặt JDK </strong>trước nếu kh&ocirc;ng sẽ gặp lỗi.</p>\r\n\r\n<p style=\"margin-left:40px\">chạy file JDK vừa tải về&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-1.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">Nhấn <strong>Next</strong> để tiếp tục&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-2.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">Tiếp theo chọn&nbsp;<strong><span style=\"color:rgb(84, 84, 84)\">I&nbsp;</span>accept the terms<span style=\"color:rgb(84, 84, 84)\">&nbsp;in the&nbsp;</span>JUnit license agreement<span style=\"color:rgb(84, 84, 84)\">.</span>. Instal JUnit</strong>&nbsp;rồi nhấn <strong>Next</strong>&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-3.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">tiếp tục nhấn <strong>Next</strong>&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-4.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">chọn đường dẫn c&agrave;i đặt, ta để mặc định</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-5.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">nhấn <strong>Install</strong> để tiến h&agrave;nh c&agrave;i đặt&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-6.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">ta đợi cho qu&aacute; tr&igrave;nh c&agrave;i đặt diễn ra</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-7.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">c&agrave;i đặt xong JDK cho NETBEANS.</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/jdk-8.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">qu&aacute; tr&igrave;nh c&agrave;i đặt JDK th&agrave;nh c&ocirc;ng.</p>\r\n\r\n<p style=\"margin-left:40px\">Tiếp theo ta thực hiện <strong>c&agrave;i đặt NETBEANS</strong> ta chạy file c&agrave;i đặt đ&atilde; tải về&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-1.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">tiếp theo nhấn <strong>Next</strong></p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-2.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">tiếp theo ta chọn<strong> I accept the term in the license agreement</strong> v&agrave; nhấn<strong> Next&nbsp;</strong></p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-3.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">chọn thư mục lưu chữ ta để mặc định rồi nhấn <strong>Next</strong>&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-4.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">bỏ dấu t&iacute;ch <strong>Check for Updates</strong> v&agrave; nhần <strong>Install</strong>&nbsp;để bắt đầu c&agrave;i đặt&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-5.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">ch&uacute;ng ta đợi cho qu&aacute; tr&igrave;nh c&agrave;i đặt diễn ra</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-6.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">v&agrave; cuối c&ugrave;ng đ&atilde; c&agrave;i đặt th&agrave;nh c&ocirc;ng&nbsp;</p>\r\n\r\n<p style=\"margin-left:40px\"><img alt=\"Cài đặt IDE netbeans phục vụ viết mã PHP\" src=\"http://hocwebsite.org/uploads/netbeans-7.PNG\" style=\"width:80%\" /></p>\r\n\r\n<p style=\"margin-left:40px\">Vậy l&agrave; ch&uacute;ng ta đ&atilde; kết th&uacute;c chương 1, c&agrave;i đặt c&aacute;c phần mềm cần thiết để học lập tr&igrave;nh website PHP.</p>\r\n\r\n<p style=\"margin-left:40px\">&nbsp;</p>\r\n\r\n<p><strong>B&agrave;i học tiếp theo</strong>&nbsp;<a href=\"http://hocwebsite.org/khai-niem-va-thuc-hanh-html-css-va-javascript\">Kh&aacute;i niệm v&agrave; thực h&agrave;nh HTML, CSS v&agrave; JAVASCRIPT</a></p>\r\n',1,'1',1456328236,'netbeans.jpg'),(6,'khai-niem-ngon-ngu-php','Khái niệm ngôn ngữ PHP ','','<p>​dang xay dung...</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n','<p style=\"text-align:justify\">​dang xay dung...</p>\r\n',1,'1',1455781800,'php.png'),(7,'cai-dat-localhost-voi-xampp-trong-10-phut','Cài đặt Localhost với Xampp trong 10 phút ','<iframe title=\"Cài đặt Localhost với Xampp\" width=\"100%\" height=\"480\" src=\"https://www.youtube.com/embed/LUZYxluVsFw\" frameborder=\"0\" allowfullscreen><p> Cài đặt Localhost với Xampp </p></iframe>','Cài đặt Server localhost với xampp, của khóa học lập trình website bằng ngôn ngữ PHP','<p style=\"text-align:justify\"><strong>Kh&aacute;i niệm đầu ti&ecirc;n</strong></p>\r\n\r\n<p style=\"text-align:justify\">Trước hết n&oacute;i về ng&ocirc;n ngữ&nbsp;PHP(<span style=\"color:rgb(37, 37, 37)\">Hypertext Preprocessor</span>) l&agrave; một ng&ocirc;n ngữ <strong>th&ocirc;ng dịch</strong>.</p>\r\n\r\n<p style=\"text-align:justify\">Tức l&agrave; thay v&igrave; chuyển đổi to&agrave;n bộ m&atilde; nguồn sang m&atilde; m&aacute;y rồi chứa v&agrave;o ổ đĩa cứng để c&oacute; thể thực thi ở lần chạy sau như ở ng&ocirc;n ngữ bi&ecirc;n dịch, th&igrave; ng&ocirc;n ngữ th&ocirc;ng dịch th&igrave; cứ khi n&agrave;o chạy chương tr&igrave;nh l&uacute;c đ&oacute; tr&igrave;nh th&ocirc;ng dịch sẽ hoạt động chuyển sang m&atilde; m&aacute;y v&agrave; đưa ra kết quả ngay. C&ocirc;ng việc n&agrave;y diễn ra tương tự ở lần chạy tiếp theo.</p>\r\n\r\n<p style=\"text-align:justify\">Về ưu điểm th&igrave; người lập tr&igrave;nh vẫn c&oacute; thể chạy chương tr&igrave;nh khi vẫn c&oacute; lỗi c&uacute; ph&aacute;p. Lỗi đến đ&acirc;u th&igrave; sẽ c&oacute; th&ocirc;ng b&aacute;o đến đ&oacute; v&agrave; bị dừng lại.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">C&ograve;n về nhược điểm th&igrave; chương tr&igrave;nh chạy bằng tr&igrave;nh th&ocirc;ng dịch kh&ocirc;ng chạy nhanh bởi v&igrave; mỗi lần chạy đều mất th&ecirc;m một qu&aacute; tr&igrave;nh dịch sang m&atilde; m&aacute;y v&agrave; thực th&igrave;.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Cụ thể với ng&ocirc;n ngữ PHP mỗi lần gọi website th&igrave; tr&igrave;nh th&ocirc;ng dịch mới bắt đầu dịch sang m&atilde; m&aacute;y v&agrave; đưa kết qủa về cho tr&igrave;nh duyệt người d&ugrave;ng. V&agrave; ta gọi n&oacute; l&agrave; <strong>WebServer</strong>, nếu như Webserver c&agrave;i đặt ở m&aacute;y c&aacute; nh&acirc;n của bạn th&igrave; n&oacute; gọi l&agrave; Localhost. Vậy <strong>Localhost</strong> ch&iacute;nh l&agrave; một Webserver nằm ở m&aacute;y t&iacute;nh c&aacute; nh&acirc;n của bạn v&agrave; đ&oacute;ng vai tr&ograve; như một tr&igrave;nh th&ocirc;ng dịch n&oacute; chứa c&aacute;c ứng dụng APACHE, MYSQL, PHP, PHPMYADMIN, ...</p>\r\n\r\n<p style=\"text-align:justify\">Phần mềm tạo Localhost th&ocirc;ng dụng tr&ecirc;n Windows hiện nay l&agrave; <strong>XAMPP</strong>, WAMP hay APPSERVER.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhi&ecirc;n m&igrave;nh khuyến kh&iacute;ch c&aacute;c bạn n&ecirc;n d&ugrave;ng XAMPP v&igrave; phần mềm n&agrave;y tương đối đơn giản để sử dụng.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>C&agrave;i đặt XAMPP</strong></p>\r\n\r\n<ol>\r\n	<li style=\"text-align:justify\">Đầu ti&ecirc;n bạn cần tải XAMPP về m&aacute;y ở <a href=\"https://www.apachefriends.org/download.html\" target=\"_blank\">đ&acirc;y</a>&nbsp;&nbsp;</li>\r\n	<li style=\"text-align:justify\">Sau đ&oacute; bạn c&agrave;i đặt b&igrave;nh thường.</li>\r\n	<li style=\"text-align:justify\">V&agrave; <strong>Start</strong> Apache với Mysql.</li>\r\n	<li style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0)\">​Sau đ&oacute; bạn thử truy cập v&agrave;o website <a href=\"http://localhost\">http://localhost&nbsp;</a></span></li>\r\n</ol>\r\n\r\n<p style=\"text-align:justify\"><strong>Nếu gặp lỗi&nbsp;</strong></p>\r\n\r\n<p style=\"text-align:justify\">Th&igrave; lỗi do xung đột cổng bạn c&oacute; thể đổi cổng 80 th&agrave;nh cổng kh&aacute;c, v&iacute; dụ th&agrave;nh 8686. V&agrave; đổi cổng 443 th&agrave;nh 4444 chẳng hạn.</p>\r\n\r\n<p style=\"text-align:justify\">L&uacute;c n&agrave;y bạn cần truy cập theo đường dẫn <a href=\"http://http://localhost:8686\">http://localhost:8686</a>&nbsp;l&agrave; được.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>B&agrave;i học tiếp theo&nbsp;</strong><a href=\"http://hocwebsite.org/cai-dat-ide-netbeans-phuc-vu-viet-ma-php\">C&agrave;i đặt NETBEANS IDE&nbsp;phục vụ viết m&atilde; PHP</a></p>\r\n',1,'1',1456294608,'xampp.png'),(8,'quan-tri-server-linux','Quản trị SERVER LINUX','','<p>Đang x&acirc;y dựng ...</p>\r\n','<p>Đang x&acirc;y dựng ...</p>\r\n',1,'1',1455771256,'server-linux.jpg'),(9,'quan-ly-sub-version-voi-git','Quản lý SUB VERSION với GIT ','','<p>Đang x&acirc;y dựng ...</p>\r\n','<p>Đang x&acirc;y dựng ...</p>\r\n',1,'1',1455771354,'github.png'),(10,'sao-luu-va-phuc-hoi-du-lieu-bao-tri-server','Sao lưu và phục hồi dữ liệu bảo trì SERVER ','','<p>Đang x&acirc;y dựng ...</p>\r\n','<p>Đang x&acirc;y dựng ...</p>\r\n',1,'1',1455771523,'saoluudulieu.gif'),(11,'kiem-soat-truy-cap-va-chong-tan-cong-ddos','Kiểm soát truy cập  và chống tấn công DDOS ','','<p>Đang x&acirc;y dựng ...</p>','<p>Đang x&acirc;y dựng ...</p>\r\n',1,'1',1456327387,'ddos.jpg'),(12,'lap-trinh-huong-doi-tuong-oop-cac-khai-niem-interface-va-abstract','Lập trình hướng đối tượng (OOP) các khái niệm INTERFACE và ABSTRACT','','<p>Đang x&acirc;y dựng ...</p>','<p>Đang x&acirc;y dựng ...</p>\r\n',1,'2',1455860354,'OOP_Objects.png'),(13,'seo-toi-uu-hoa-cong-cu-tim-kiem','SEO tối ưu hóa công cụ tìm kiếm ','đang xây dựng ','Kỹ thuật tối ưu hóa SEO, các cách SEO, các công cụ phục vụ cho SEO','<p>Đang x&acirc;y dựng&nbsp;</p>\r\n',1,'1',1456215842,'SEO.png');
